<?php    namespace Website\Model\Manager;    class Manager extends DatabaseManager  {    private $bdd;        public function __construct()    {      $this->bdd = parent::getBDD();    }        /**     * @return mixed     */    public function getBonjour()    {            $id = rand(1, 20);      $sql = $this->bdd->prepare('SELECT * FROM bonjour WHERE id = :id');      $sql->bindValue(':id', $id);      $sql->execute();            return $sql->fetch();    }        /**     * @param $adresse_ip     * @return bool     */    public function getLastVisite($adresse_ip)    {      $sql = $this->bdd->prepare('SELECT id,DATE_FORMAT(date_visite, \'%d/%m/%Y &agrave; %Hh%i\') AS date_visite        FROM visite WHERE adresse_ip = :ip ORDER BY id LIMIT 1,1');      $sql->bindValue(':ip', $adresse_ip);      $sql->execute();            $last_visite = ($donnees = $sql->fetch()) ? $donnees['date_visite'] : FALSE;            return $last_visite;    }  }