<?php session_start();// verif de l'existence des variablesif (isset($_POST['nombre'])){	$n=ltrim($_POST['nombre'],"0");	if ( $n<0||!is_numeric($n) )	{		$reponse='Il faut un entier positif !!!';	}	else	{		if ( $n==0 )		{			$reponse='F(0) = 0';		}		elseif ( $n==1)		{			$reponse='F(1) = 1<br />';		}		elseif ( $n>1480 )		{			$reponse='F('.$n.') = INF ';		}		else		{			$F[0]=0;			$F[1]=1;			$i=2;			while( $i<=$n )			{				$F[$i]=$F[$i-1]+$F[$i-2];				$i++;			}			$reponse='F('.$n.') = '.$F[$n].'';		}	}	echo $reponse;}else{	header('Location: ../script.php');}