<?php session_start() ?><!DOCTYPE html><html><head><link rel="stylesheet" href="../style.css" /><title>Un peu de PHP</title><meta charset="utf-8"><link rel="icon" type="image/png" href="../img/favicon.png" /><?phpif (isset($_POST['nombre']))	{	$n=ltrim($_POST['nombre'],"0");	if ($n>0&&is_int($n/1))		{		if ( $n>10000000 )			{			$reponse='Le temps de calcul est trop grand : choisissez un nombre plus petit';			}			else			{			$a=0;			$b=1;			while ($b<=$n)				{				$a=$a+$b;				$b++;				}			$reponse='La somme des entiers de 1 &agrave; '.$n.' est '.$a.'';			}		}	else		{		$reponse='Il faut choisir un entier strictement positif !';		}	$_SESSION['reponse'] = $reponse;	}header('Location: ../script.php');?></head><body></body></html>