<?php session_start();if (isset($_POST['nombre']) AND isset($_POST['position']) AND isset($_POST['combien'])){	$a=ltrim($_POST['nombre'],"0");	$position = $_POST['position'];	$combien = (is_int($_POST['combien']/1)) ? $_POST['combien'] : 10;	$combien = ($combien > 1) ? $combien : 1 ;	$combien = ($combien < 100) ? $combien : 100 ;	if (!is_numeric($a)||!is_int($a/1))	{		$reponse='Il faut un nombre entier !!';	}	elseif ($a < 0)	{		$reponse='Il faut un entier positif !!';	}	else	{		if ($position=='av')		{			$count_av = $combien;			$count_ap = 0;		}		elseif ($position=='ap')		{			$count_av = 0;			$count_ap = $combien;		}		else		{			if ($combien%2==0)			{				$count_av = ($combien/2);				$count_ap = ($combien/2);			}			else			{				$count_av = (($combien+1)/2);				$count_ap = (($combien-1)/2);			}		}		$liste_av = [];		$liste_ap = [];		// avant		$count = 0;		$n = $a - 1 ;		while (($count < $count_av) AND ($n > 1 ) )		{			$i=2;			while ($n%$i!=0 AND $i<sqrt($n))			{				$i++;			}			if ($i>sqrt($n))			{				$count++;				array_push ($liste_av , $n);							}			$n = $n -1 ;		}		// apres		$count = 0;		$n = $a + 1 ;		while ($count < $count_ap )		{			$i=2;			while ($n%$i!=0 AND $i<sqrt($n))			{				$i++;			}			if ($i>sqrt($n))			{				$count++;				array_push ($liste_ap , $n);			}			$n = $n + 1 ;		}		$reponse = '';		// liste avant		$liste_av = array_reverse($liste_av);		foreach ($liste_av as $nombre)		{			$reponse .= ' '.$nombre.' - ';		}		$reponse.= ' <span class="gras"> '.$a.' </span> ';		// liste apres		foreach ($liste_ap as $nombre)		{			$reponse .= ' - '.$nombre.' ';		}	}echo $reponse;}