<?php session_start();if (isset($_POST['nombre1'])&&isset($_POST['nombre2'])&&isset($_POST['nombre3'])){	$a=$_POST['nombre1'];	$b=$_POST['nombre2'];	$c=$_POST['nombre3'];	if (!is_numeric($a)||!is_numeric($b)||!is_numeric($c))	{		$reponse='Il faut trois nombres r&eacute;els!';	}	elseif ($a==0&&$b==0&&$c==0)	{		$reponse='S = {&real;}';	}	elseif ($a==0&&$b==0&&$c!=0)	{		$reponse='S = {&Oslash;}';	}	elseif ($a==0&&$b!=0)	{		$x=(-$c)/($b);		$reponse='S = {'.$x.'}';	}	else	{		$d=($b*$b)-4*$a*$c;		if ($d<0)		{			$reponse='S = {&Oslash;}';		}		elseif ($d==0)		{			$x=(-$b)/(2*$a);			$reponse='S = {'.$x.'}';		}		else		{			$x1=(-$b-sqrt($d))/(2*$a);			$x2=(-$b+sqrt($d))/(2*$a);			$reponse='S = {'.$x1.' , '.$x2.'}';		}	}	echo $reponse;}