<?php session_start();// traitement formulaireif (isset($_POST['n'])&&isset($_POST['a'])&&isset($_POST['b'])&&isset($_POST['c'])){	$n = ($_POST['n']==0) ? 0 : ltrim($_POST['n'],"0");	$a = ($_POST['a']==0) ? 0 : ltrim($_POST['a'],"0");	$b = ($_POST['b']==0) ? 0 : ltrim($_POST['b'],"0");	$c = ($_POST['c']==0) ? 0 : ltrim($_POST['c'],"0");	if (!is_numeric($a)||!is_numeric($b)||!is_numeric($c)||!is_numeric($n))	{		$reponse='Il faut trois nombres';	}	elseif ($n<=0)	{		$reponse='Il faut que le nombre de termes &agrave; afficher soit positif !';	}	elseif (!is_int($n/1))	{		$reponse='Il faut que le nombre de termes &agrave; afficher soit entier !';	}	elseif ($n > 200)	{		$reponse='Il faut que le nombre de termes &agrave; afficher soit raisonnable !';	}	elseif ($a==0)	{		$reponse='La suite que vous avez d&eacutefini est constante  &agrave; partir de  n = 1 et <br />		quelque soit l\'entier n > 0, U<sub>n</sub> = '.$b.'';	}	elseif ($n==1)	{		$reponse='il n\'y a pas besoin de demander pour conna&icirc;tre uniquement le premier terme, <br /> c\'est vous qui l\'avez d&eacutefini :';		$U[1]=($a*$c)+$b;		$reponse.='U<sub>1</sub> = '.$a.' x '.$c.' + '.$b.' soit '.$U[1].'';	}	else	{		$reponse='Votre suite est d&eacute;fini par : <br /> U<sub>0</sub> = '.$c.' et U<sub>n+1</sub> = '.$a.' U<sub>n</sub> + '.$b.'<br />';		$reponse.=' Voici les '.$n.' premiers termes de cette suite : <br />';		$U[0]=$c;		$i=1;		while ($i<$n+1)		{			$U[$i]=$a*$U[$i-1]+$b;			$reponse.='U<sub>'.$i.'</sub> = '.$U[$i].'<br />';			$i++;		}	}	echo $reponse;}else{header('Location: ../script.php');}